extractorFunction.default:
function extractMetadata(input, cheerio) {
  let $ = cheerio.load(input);
  let metadata = {};
  $('meta').each(function() {
    let name = $(this).attr('name') || $(this).attr('property');
    if (name) {
      metadata[name] = $(this).attr('content');
    }
  });
  return { metadata };
}
